/* Body setup to horizontally center everything */
body {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  /*background-color: #f8f8f8;*/
  gap: 70px;
  /* Space between carousels */
}

/* Carousel container dimensions */
.carousel {
  width: 300px;
  height: 300px;
  position: relative;
  margin: 0;
  padding: 0;
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}

/* Ensure images fit nicely in the carousel */
.slick-slide img {
  width: 100%;
  height: 100%;
  width: 300px;
  height: 300px;
  /*object-fit: cover;*/
}

/* Hide the arrows initially and show on hover */
.slick-prev,
.slick-next {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  color: white;
  opacity: 0;
  transition: opacity 0.3s;
  z-index: 1;
}

.carousel:hover .slick-prev,
.carousel:hover .slick-next {
  opacity: 1;
}

.slick-prev {
  left: 10px;
}

.slick-next {
  right: 10px;
}

/* Adjust the position of dots inside the image */
.slick-dots {
  position: absolute;
  bottom: 10px;
  /* Adjust vertical position of dots */
  width: 100%;
  display: flex;
  justify-content: center;
  padding: 0;
  margin: 0;
  z-index: 10;
  /* Make sure dots are on top of the image */
}

.slick-dots li button:before {
  font-size: 7px;
  color: white;
  /* Dot color inside the image */
  opacity: 0.75;
}

/* Optional: Make the dots bigger when active */
.slick-dots li.slick-active button:before {
  opacity: 1;
}

