srcDoc.css:
body {
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #fff;
  border-style: none;
  margin: 0;
}

#slick-container {
  width: 90%;
  background-color: #fff;
  border-style: none;
  margin: 0;
}

.slick-prev:before,
.slick-next:before,
.slick-dots {
  color: #e9e2e2;
}

#slick div {
  padding-top: 5px;
  padding-bottom: 5px;
  width: 300px; /* Example width */
  height: 300px;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: white;
  border-style: none;
  border-radius: 5;
  margin: 0;
}

#slick div img {
  max-width: 100%;
  max-height: 100%;
  height: auto;
  width: auto;
  background-color: #fff;
  border-style: none;
  margin: 0;
  width: 300px; /* Example width */
  height: 600px;
  border-radius: 15px;
}

.slick-next {
  left: 90%;
}

.slick-prev {
  left: 5%;
  z-index: 1;
}

.slick-dots {
  top: 80%;
  padding: 0;
  margin: 0;
}

